package net.treimers.square1.view;

/**
 *  <p><img src="data:image/png;base64," />

 *
 */

public class CornerPiece extends AbstractPiece {
	private static final int A = 0;
	private static final int B = 1;
	private static final int C = 2;
	private static final int D = 3;
	private static final int E = 4;
	private static final int F = 5;
	private static final int G = 6;
	private static final int H = 7;

	public CornerPiece(float size, int rotate, int position, int... colors) {
		float s = (float) (size * Math.sin(Constants.ANGLE_15));
		float edgeWidth = (float) (2 * size * Math.sin(Constants.ANGLE_15));
		float cornerWidth = (float) (size - edgeWidth / 2.0f);
		float[] points = {
			// Point A
			0.0f,
			position * edgeWidth / 2.0f,
			0.0f,
			// Point B
			size,
			position * edgeWidth / 2.0f,
			s,
			// Point C
			size,
			position * edgeWidth / 2.0f,
			size,
			// Point D
			s,
			position * edgeWidth / 2.0f,
			size,
			// Point E
			0.0f,
			position * (cornerWidth + edgeWidth / 2.0f),
			0.0f,
			// Point F
			size,
			position * (cornerWidth + edgeWidth / 2.0f),
			s,
			// Point G
			size,
			position * (cornerWidth + edgeWidth / 2.0f),
			size,
			// Point H
			s,
			position * (cornerWidth + edgeWidth / 2.0f),
			size,
		};
		addAllPoints(points);
		int[] faces = {
			// Bottom face
			A,
			colors[0],
			D,
			colors[0],
			C,
			colors[0],
			C,
			colors[0],
			B,
			colors[0],
			A,
			colors[0],
			// Left rear face
			A,
			colors[1],
			B,
			colors[1],
			F,
			colors[1],
			F,
			colors[1],
			E,
			colors[1],
			A,
			colors[1],
			// Left front face
			B,
			colors[2],
			C,
			colors[2],
			G,
			colors[2],
			G,
			colors[2],
			F,
			colors[2],
			B,
			colors[2],
			// Right front face
			G,
			colors[3],
			C,
			colors[3],
			D,
			colors[3],
			D,
			colors[3],
			H,
			colors[3],
			G,
			colors[3],
			// Right rear face
			E,
			colors[4],
			H,
			colors[4],
			D,
			colors[4],
			D,
			colors[4],
			A,
			colors[4],
			E,
			colors[4],
			// Top face
			E,
			colors[5],
			F,
			colors[5],
			G,
			colors[5],
			G,
			colors[5],
			H,
			colors[5],
			E,
			colors[5],
		};
		addAllFaces(faces);
		rotateByY(90 * rotate);
	}
}