package net.treimers.square1.view;

/**
 * <p>Instances of this class represent a Square-1 corner piece.
 *
 * <p>A corner piece has three visible, colored faces: top, front and bottom or top. All other faces are
 * not visible by default and colored light or dark gray.
 * 
 * <p>The piece is created using 8 corner points named A to H. All faces are build and colored using triangles based on the corners.
 * <ul>
 * <li>bottom: D, C, B, A</li>
 * <li>left rear: F, E, A, B</li>
 * <li>left front: C, G, F, B</li>
 * <li>right front: C, D, H, G</li>
 * <li>right rear: A, E, H, D</li>
 * <li>top: E, F, G, H</li>
 * </ul>
 * 
 * <p><img src="data:image/png;base64," />
 */
public class CornerPiece extends AbstractPiece {
	/** Index of point A. */
	private static final int A = 0;
	/** Index of point B. */
	private static final int B = 1;
	/** Index of point C. */
	private static final int C = 2;
	/** Index of point D. */
	private static final int D = 3;
	/** Index of point E. */
	private static final int E = 4;
	/** Index of point F. */
	private static final int F = 5;
	/** Index of point G. */
	private static final int G = 6;
	/** Index of point H. */
	private static final int H = 7;

	/**
	 * Creates a new instance.
	 * @param rotate value to rotate piece around top (allowed values are 0 for no rotation, 1 for 90 degrees, 2 for 180 degrees
	 * or 3 for 270 degrees).
	 * @param colors array with colors for the 6 sides (bottom, left rear, left front, right front, right rear, top).
	 */
	public CornerPiece(int rotate, int position, int... colors) {
		float[] points = {
			// Point A
			0.0f + Constants.DELTA,
			0.0f - Constants.DELTA,
			-position * (Constants.EDGE_WIDTH / 2.0f + Constants.DELTA),
			// Point B
			Constants.SIZE,
			-Constants.S - Constants.DELTA,
			-position * (Constants.EDGE_WIDTH / 2.0f + Constants.DELTA),
			// Point C
			Constants.SIZE,
			-Constants.SIZE,
			-position * (Constants.EDGE_WIDTH / 2.0f + Constants.DELTA),
			// Point D
			Constants.S + Constants.DELTA,
			-Constants.SIZE,
			-position * (Constants.EDGE_WIDTH / 2.0f + Constants.DELTA),
			// Point E
			0.0f + Constants.DELTA,
			0.0f - Constants.DELTA,
			-position * (Constants.CORNER_WIDTH + Constants.EDGE_WIDTH / 2.0f - Constants.DELTA),
			// Point F
			Constants.SIZE,
			-Constants.S - Constants.DELTA,
			-position * (Constants.CORNER_WIDTH + Constants.EDGE_WIDTH / 2.0f - Constants.DELTA),
			// Point G
			Constants.SIZE,
			-Constants.SIZE,
			-position * (Constants.CORNER_WIDTH + Constants.EDGE_WIDTH / 2.0f - Constants.DELTA),
			// Point H
			Constants.S + Constants.DELTA,
			-Constants.SIZE,
			-position * (Constants.CORNER_WIDTH + Constants.EDGE_WIDTH / 2.0f - Constants.DELTA),
		};
		addAllPoints(points);
		int[] faces = {
			// Bottom face
			A,
			colors[0],
			D,
			colors[0],
			C,
			colors[0],
			C,
			colors[0],
			B,
			colors[0],
			A,
			colors[0],
			// Left rear face
			A,
			colors[1],
			B,
			colors[1],
			F,
			colors[1],
			F,
			colors[1],
			E,
			colors[1],
			A,
			colors[1],
			// Left front face
			B,
			colors[2],
			C,
			colors[2],
			G,
			colors[2],
			G,
			colors[2],
			F,
			colors[2],
			B,
			colors[2],
			// Right front face
			G,
			colors[3],
			C,
			colors[3],
			D,
			colors[3],
			D,
			colors[3],
			H,
			colors[3],
			G,
			colors[3],
			// Right rear face
			E,
			colors[4],
			H,
			colors[4],
			D,
			colors[4],
			D,
			colors[4],
			A,
			colors[4],
			E,
			colors[4],
			// Top face
			E,
			colors[5],
			F,
			colors[5],
			G,
			colors[5],
			G,
			colors[5],
			H,
			colors[5],
			E,
			colors[5],
		};
		addAllFaces(faces);
		rotateByZ(90 * rotate);
	}
}