package net.treimers.square1.view.piece;

import net.treimers.square1.model.ColorBean;
import net.treimers.square1.model.Constants;

/**
 * <p>Instances of this class represent a Square-1 edge piece.
 *
 * <p>An edge piece has two visible, colored faces: top and front. All other faces are
 * not visible by default and colored light or dark gray.
 * 
 * <p>The piece is created using 6 corner points named A to F. All faces are build and colored using triangles based on the corners.
 * <ul>
 * <li>bottom: C, B, A</li>
 * <li>left: A, B, E, D</li>
 * <li>front: B, C, F, E</li>
 * <li>right 1: D, F, C, A</li>
 * <li>top: D, E, F</li>
 * </ul>
 * 
 * <p><img src="data:image/png;base64," />
 * 
 * <p>The edge width i.e. the distance between B and C is calculated and defined in the Constants class. 
 */
public abstract class AbstractEdgePiece extends AbstractPiece {
	/** Index of point A. */
	private static final int POINT_A = 0;
	/** Index of point B. */
	private static final int POINT_B = 1;
	/** Index of point C. */
	private static final int POINT_C = 2;
	/** Index of point D. */
	private static final int POINT_D = 3;
	/** Index of point E. */
	private static final int POINT_E = 4;
	/** Index of point F. */
	private static final int POINT_F = 5;

	/**
	 * Creates a new instance.
	 * @param rotate value to rotate piece around top (allowed values are 0 for no rotation, 1 for 90 degrees, 2 for 180 degrees
	 * or 3 for 270 degrees).
	 * @param sides array with colors for the 5 sides (bottom, left, front, right, top).
	 */
	public AbstractEdgePiece(int rotate, int position, ColorBean colorBean, int... sides) {
		super(colorBean);
		float[] points = {
			// Point A
			0.0f,
			0.0f,
			-position * (Constants.EDGE_WIDTH / 2.0f + Constants.DELTA),
			// Point B
			Constants.SIZE,
			Constants.EDGE_WIDTH / 2.0f - Constants.DELTA,
			-position * (Constants.EDGE_WIDTH / 2.0f + Constants.DELTA),
			// Point C
			Constants.SIZE,
			-Constants.EDGE_WIDTH / 2.0f + Constants.DELTA,
			-position * (Constants.EDGE_WIDTH / 2.0f + Constants.DELTA),
			// Point D
			0.0f + Constants.DELTA,
			0.0f,
			-position * (Constants.CORNER_WIDTH + Constants.EDGE_WIDTH / 2.0f - Constants.DELTA),
			// Point E
			Constants.SIZE,
			Constants.EDGE_WIDTH / 2.0f - Constants.DELTA,
			-position * (Constants.CORNER_WIDTH + Constants.EDGE_WIDTH / 2.0f - Constants.DELTA),
			// Point F
			Constants.SIZE,
			-Constants.EDGE_WIDTH / 2.0f + Constants.DELTA,
			-position * (Constants.CORNER_WIDTH + Constants.EDGE_WIDTH / 2.0f - Constants.DELTA),
		};
		addAllPoints(points);
		int[] faces = {
			// Bottom face
			POINT_A,
			sides[0],
			POINT_C,
			sides[0],
			POINT_B,
			sides[0],
			// Left face
			POINT_A,
			sides[1],
			POINT_B,
			sides[1],
			POINT_E,
			sides[1],
			POINT_E,
			sides[1],
			POINT_D,
			sides[1],
			POINT_A,
			sides[1],
			// Front face
			POINT_E,
			sides[2],
			POINT_B,
			sides[2],
			POINT_C,
			sides[2],
			POINT_C,
			sides[2],
			POINT_F,
			sides[2],
			POINT_E,
			sides[2],
			// Right face
			POINT_D,
			sides[3],
			POINT_F,
			sides[3],
			POINT_C,
			sides[3],
			POINT_C,
			sides[3],
			POINT_A,
			sides[3],
			POINT_D,
			sides[3],
			// Top face
			POINT_D,
			sides[4],
			POINT_E,
			sides[4],
			POINT_F,
			sides[4],
		};
		addAllFaces(faces);
		rotateByZ(90 * rotate);
	}
}