package net.treimers.square1.view.piece;

import net.treimers.square1.controller.ColorBean;
import net.treimers.square1.controller.Constants;

/**
 * <p>Instances of this class represent a Square-1 middle piece.
 *
 * <p>A middle piece has three visible, colored faces: left, front and rear. All other faces are
 * not visible by default and colored light or dark gray.
 * 
 * <p>The piece is created using 8 corner points named A to H. All faces are build and colored using triangles based on the corners.
 * <ul>
 * <li>bottom: D, C, B, A</li>
 * <li>left: F, E, A, B</li>
 * <li>front: C, G, F, B</li>
 * <li>right: C, D, H, G</li>
 * <li>rear: E, H, D, A</li>
 * <li>top: E, F, G, H</li>
 * </ul>
 * 
 * <p><img src="data:image/png;base64," />
 */
public abstract class AbstractMiddlePiece extends AbstractPiece {
	/** Index of point A. */
	private static final int POINT_A = 0;
	/** Index of point B. */
	private static final int POINT_B = 1;
	/** Index of point C. */
	private static final int POINT_C = 2;
	/** Index of point D. */
	private static final int POINT_D = 3;
	/** Index of point E. */
	private static final int POINT_E = 4;
	/** Index of point F. */
	private static final int POINT_F = 5;
	/** Index of point G. */
	private static final int POINT_G = 6;
	/** Index of point H. */
	private static final int POINT_H = 7;

	/**
	 * Creates a new instance.
	 * @param rotate value to rotate piece around top (allowed values are 0 for no rotation, 1 for 90 degrees, 2 for 180 degrees
	 * or 3 for 270 degrees).
	 * @param sides array with colors for the 6 sides (bottom, left, front, right, rear, top).
	 */
	public AbstractMiddlePiece(int rotate, ColorBean colorBean, int... sides) {
		super(colorBean);
		float[] points = {
			// Point A
			Constants.SIZE,
			Constants.SIZE,
			Constants.EDGE_WIDTH / 2.0f - Constants.DELTA,
			// Point B
			Constants.SIZE,
			-Constants.SIZE,
			Constants.EDGE_WIDTH / 2.0f - Constants.DELTA,
			// Point C
			(float) (Constants.SIZE * Math.sin(Constants.ANGLE_15)) + Constants.DELTA,
			-Constants.SIZE,
			Constants.EDGE_WIDTH / 2.0f - Constants.DELTA,
			// Point D
			(float) (-Constants.SIZE * Math.sin(Constants.ANGLE_15)) + Constants.DELTA,
			Constants.SIZE,
			Constants.EDGE_WIDTH / 2.0f - Constants.DELTA,
			// Point E
			Constants.SIZE,
			Constants.SIZE,
			-Constants.EDGE_WIDTH / 2.0f + Constants.DELTA,
			// Point F
			Constants.SIZE,
			-Constants.SIZE,
			-Constants.EDGE_WIDTH / 2.0f + Constants.DELTA,
			// Point G
			(float) (Constants.SIZE * Math.sin(Constants.ANGLE_15)) + Constants.DELTA,
			-Constants.SIZE,
			-Constants.EDGE_WIDTH / 2.0f + Constants.DELTA,
			// Point H
			(float) (-Constants.SIZE * Math.sin(Constants.ANGLE_15)) + Constants.DELTA,
			Constants.SIZE,
			-Constants.EDGE_WIDTH / 2.0f + Constants.DELTA,
		};
		addAllPoints(points);
		int[] faces = {
			// Faces
			// Bottom face
			POINT_A,
			sides[0],
			POINT_D,
			sides[0],
			POINT_C,
			sides[0],
			POINT_C,
			sides[0],
			POINT_B,
			sides[0],
			POINT_A,
			sides[0],
			// Left face
			POINT_A,
			sides[1],
			POINT_B,
			sides[1],
			POINT_F,
			sides[1],
			POINT_F,
			sides[1],
			POINT_E,
			sides[1],
			POINT_A,
			sides[1],
			// Front face
			POINT_F,
			sides[2],
			POINT_B,
			sides[2],
			POINT_C,
			sides[2],
			POINT_C,
			sides[2],
			POINT_G,
			sides[2],
			POINT_F,
			sides[2],
			// Right face
			POINT_D,
			sides[3],
			POINT_H,
			sides[3],
			POINT_G,
			sides[3],
			POINT_G,
			sides[3],
			POINT_C,
			sides[3],
			POINT_D,
			sides[3],
			// Rear face
			POINT_A,
			sides[4],
			POINT_E,
			sides[4],
			POINT_H,
			sides[4],
			POINT_H,
			sides[4],
			POINT_D,
			sides[4],
			POINT_A,
			sides[4],
			// Top face
			POINT_E,
			sides[5],
			POINT_F,
			sides[5],
			POINT_G,
			sides[5],
			POINT_G,
			sides[5],
			POINT_H,
			sides[5],
			POINT_E,
			sides[5],
		};
		addAllFaces(faces);
		rotateByZ(rotate * 180);
	}
}